package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=25;
		if(number%2==0) {
			System.out.println("The given input is Even");
		}
		else {
			System.out.println("The given input is Odd");
		}
	}

}
